import React from 'react';

//const inicialTxt = "Hello World!"
//Função com a data
function DateSet() {
    const date = new Date();
    const dia = date.getDate();
    const mes = date.getMonth() + 1;
    const ano = date.getFullYear();    
    const fullDate = dia + '/' + mes + '/' + ano;
    return fullDate;
}
//Função com os tipos de textos
function ListaTexto(props) {
   
    function txtNormal(t) {
        t = inicialTxt
        return t;
    }    

    function txtUpperCase(t) {
        const newT = t.toUpperCase();
        return newT
    }

    function txtLowerCase(t) {
        const newT = t.toLowerCase();
        return newT
    }

    function txtInvertWorld(t) {
        const newT = t.split('').reverse().join('');
        return newT
    }

    function txtChargeWorlds(t) {
        const newT = t.split(" ").reverse().join(" ");
        return newT
    }

    function txtLowerAndUpperCase(t) {
        let newT = '';
        for (let i = 0; i < t.length; i++) {
            if (i % 2 == 0) {
                newT += t.charAt(i).toLowerCase();
            } else {
                newT += t.charAt(i).toUpperCase();
            }
        }
        return newT;
    }
    
    function txtAdd(t) {
        const newT = t.repeat(2).split('').reverse();
        return newT
    }

    function txtInvert(t) {
        const newT = t.split(" ").reverse().join(" ").split("").reverse().join("");
        return newT
    }

    function txtSpaced(t) {
        const newT = t.split("").join("  ") + DateSet();
        return newT
    }

    function txtArrow(t) {
        let newT = t.split('').reverse().join('-').replace(' ', '') + DateSet();
        return newT
    }
 
    const TextsArray = [
        txtNormal(props.text) + " " + DateSet(),
        txtUpperCase(props.name) + " " + DateSet(),
        txtLowerCase(props.name) + " " + DateSet(),
        txtInvertWorld(props.name) + " " + DateSet(),
        txtChargeWorlds(props.name) + " " + DateSet(),
        txtLowerAndUpperCase(props.name) + " " + DateSet(),
        txtAdd(props.name) + "  " + DateSet(),
        txtInvert(props.name) + " " + DateSet(),
        txtSpaced(props.name) + " " + DateSet(),
        txtArrow(props.name) + " " + DateSet()
    ]
   
    const itens = TextsArray.map((data, index) => {
        return <p key={index}>{data}</p>
    })
    return (
        <dir id="showList">
            {itens}
        </dir>
    )
}

export default ListaTexto
