import React from 'react';

const txtMain = "Hello World!"
//Cria uma função com a data e hora 

function DataText() {
    const datte = new Date();
    const dia = datte.getDate();
    const mes = datte.getMonth() + 1;
    const ano = datte.getFullYear();    
    const dateComple = dia + '/'+ mes +'/'+ ano;
    return dateComple;
}
//Cria uma função com todos os estilos
function ListWords(props) {
    /*
        Cada texto tem uma funcao onde tem o objetivo
        de facilitar a manutenção do codigo
    */
    function txtNormal(txtShow) {
        txtShow = txtMain
        return txtShow;
    }
    function txtUpperCase(txtShow) {
        let rec = txtShow.toUpperCase();
        return rec
    }
    function txtLowerCase(txtShow) {
        let rec = txtShow.toLowerCase();
        return rec
    }
    function txtInvertWorld(txtShow) {
        let rec = txtShow.split('').reverse().join('');
        return rec
    }
    function txtChargeWorlds(txtShow) {
        let rec = txtShow.split(" ").reverse().join(" ");
        return rec
    }
    function txtLowerAndUpperCase(txtShow) {
        let rec = '';
        for (let i = 0; i < txtShow.length; i++) {
            if (i % 2 === 1) {
                rec += txtShow.charAt(i).toLowerCase();
            } else {
                rec += txtShow.charAt(i).toUpperCase();
            }
        }
        return rec;
    }
    function txtAdd(txtShow) {
        let rec = txtShow.repeat(2).split('').reverse();
        return rec
    }
    function txtInvert(txtShow) {
        let rec = txtShow.split(" ").reverse().join(" ").split("").reverse().join("");
        return rec
    }
    function txtSpaced(txtShow) {
        let rec = txtShow.split("").join("  ") + DataText();
        return rec
    }
    function txtArrow(txtShow) {
        let rec = txtShow.split("").join(" → ") +  DataText();
        return rec
    }
    //Cria o array com todos os dados
    const WorldsArray = [
        txtNormal(props.text) + " " + DataText(),
        txtUpperCase(props.name) + " " + DataText(),
        txtLowerCase(props.name) + " " + DataText(),
        txtInvertWorld(props.name) + " " + DataText(),
        txtChargeWorlds(props.name) + " " + DataText(),
        txtLowerAndUpperCase(props.name) + " " + DataText(),
        txtAdd(props.name) + "  " + DataText(),
        txtInvert(props.name) + " " + DataText(),
        txtSpaced(props.name) + " " + DataText(),
        txtArrow(props.name) + " " + DataText()
    ]
    //Cria o map do array e estrutura a base
    const items = WorldsArray.map((data, index) => {
        return <p key={index}>{data}</p>
    })
    return (
        <dir id="showList">
            {items}
        </dir>
    )
}

export default ListWords
